	@SuppressWarnings("deprecation")
	public static int getScreenWidth(Activity activity) {
		Display display = activity.getWindowManager().getDefaultDisplay(); 
		int screenWidth = display.getWidth();
		return screenWidth;
	}
	

	@SuppressWarnings("deprecation")
	public static int getScreenHeight(Activity activity) {
		Display display = activity.getWindowManager().getDefaultDisplay(); 
		int screenHeight = display.getHeight();
		return screenHeight;
	}

	
	private static Drawable getDrawableFromHexStr(Resources res, String hex) {
		byte[] 		drawableByte = OAuthLoginImage.hexToByteArray(hex);
		Bitmap 		bitmap = null;
        Drawable 	bt = null;
		int 		densityDpi = res.getDisplayMetrics().densityDpi;
		
        try {
            bitmap = BitmapFactory.decodeByteArray(drawableByte, 0, drawableByte.length);
        } catch (Exception e) {
            BitmapFactory.Options options=new BitmapFactory.Options();// Create object of bitmapfactory's option method for further option use
            bitmap = BitmapFactory.decodeByteArray(drawableByte, 0, drawableByte.length, options);
            e.printStackTrace();
        }

		try {
	        // 원래 이미지가 xhdpi 용이기 때문에 다른 dpi 에선 이미지 사이즈 줄여줘야 함.	        
	        if (DeviceDisplayInfo.isXhdpi(densityDpi)) {
	        	bt = new BitmapDrawable(res, bitmap);
	        } else if (DeviceDisplayInfo.isHdpi(densityDpi)) {
	        	int newWidth = (int) (bitmap.getWidth() * 0.5);
	        	int newHeight = (int) (bitmap.getHeight() * 0.5);
	        	bt = new BitmapDrawable(res, Bitmap.createScaledBitmap(bitmap, newWidth, newHeight, true));
	        } else {
	        	int newWidth = (int) (bitmap.getWidth() * 0.33);
	        	int newHeight = (int) (bitmap.getHeight() * 0.33);
	        	bt = new BitmapDrawable(res, Bitmap.createScaledBitmap(bitmap, newWidth, newHeight, true));
	        }
		} catch (Exception e) {
			e.printStackTrace();
		}

		return bt;
	}
	
	public static Drawable getDrawableCloseImg(Resources res) {
		return getDrawableFromHexStr(res, OAuthLoginImage.getDrawableByteStrCloseBtnImg());
	}
	
	public static Drawable getDrawableNaverIcon(Resources res) {
		return getDrawableFromHexStr(res, OAuthLoginImage.getDrawableByteStrNaverIconImg());
	}
	
	/*
	 * ref : http://stackoverflow.com/questions/4605527/converting-pixels-to-dp-in-android
	 * This method converts dp unit to equivalent pixels, depending on device density. 
	 * 
	 * @param dp A value in dp (density independent pixels) unit. Which we need to convert into pixels
	 * @param context Context to get resources and device specific display metrics
	 * @return A float value to represent px equivalent to dp depending on device density
	 */
	public static float convertDpToPixel(float dp, Context context){
	    Resources resources = context.getResources();
	    DisplayMetrics metrics = resources.getDisplayMetrics();
	    float px = dp * (metrics.densityDpi / 160f);
	    return px;
	}

